<script lang="ts">
	import Logo from '$lib/img/hasc.png';
	import Game from '$lib/img/game.svg';
	import Gear from '$lib/img/gear.svg';
	import ArrowLeftRigth from '$lib/img/arrow-left-right.svg';
	import Microchip from '$lib/img/microchip.svg';
	import { State, Connect, Error, NodeIds, type Value } from '$lib/store/state';
	import { onMount } from 'svelte';
	import { PUBLIC_ENVOY_ACTIVE_INVERTERS } from '$env/static/public';
	import { PUBLIC_ENVOY_NET_INSTANT_CONSUMPTION } from '$env/static/public';
	import { PUBLIC_ENVOY_TOTAL_INSTANT_CONSUMPTION } from '$env/static/public';
	import { PUBLIC_BOILER_CONSUMPTION } from '$env/static/public';
	import { PUBLIC_BOILER_TEMPERATURE } from '$env/static/public';
	import { PUBLIC_BOILER_FLOW } from '$env/static/public';
	import { PUBLIC_TEMPERATURE } from '$env/static/public';
	import { PUBLIC_HUMIDITY } from '$env/static/public';
	import { PUBLIC_VMC_STATE } from '$env/static/public';
	import { PUBLIC_GARAGE_STATE } from '$env/static/public';
	import { PUBLIC_API_ENDPOINT } from '$env/static/public';
	import { WritableTokenStore } from '$lib/store/auth';
	import { Type } from '$lib/utils/types';
	import { BackToLogin } from '$lib/route/route';
	import { goto } from '$app/navigation';
	import { base } from '$app/paths';
	import Cookies from 'js-cookie';

	let token = '';
	let netError = false;

	WritableTokenStore.subscribe((t) => {
		token = t;
	});

	let states: Map<string, Value>;

	let ActiveInverters = 0;
	let InstantProduction = 0;
	let NetInstantConsumption = 0;
	let TotalInstantConsumption = 0;
	let HomeConsumption = 0;
	let BoilerConsumption = 0;
	let HeaterConsumption = 0;
	let CarConsumption = 0;
	let OtherConsumption = 0;
	let BoilerTemperature = 0;
	let BoilerFlow = 0;
	let RoomTemperature = 0;
	let Temperature = 0;
	let Humidity = 0;
	let GarageState = 0;

	let InstantProductionStyle = '';
	let BoilerConsumptionStyle = '';
	let HeaterConsumptionStyle = '';
	let CarConsumptionStyle = '';
	let NetInstantConsumptionStyle = '';
	let OtherConsumptionStyle = '';
	let BoilerFlowStyle = '';
	let VMCState = false;

	let Red = 'rgb(232, 19, 19)';
	let Green = 'rgb(0, 138, 14)';
	let Orange = 'rgb(252, 148, 50)';

	let HomeColor = Green;

	let settings = false;

	Error.subscribe((err) => {
		if (err) {
			BackToLogin();
		}
	});

	let parseFloatPath = (states: Map<string, Value>, path: string): number => {
		return Math.floor(parseFloat(states?.get(path)?.Value || '0'));
	};

	let parseBooleanPath = (states: Map<string, Value>, path: string): boolean => {
		return (states?.get(path)?.Value || 'false') == 'true';
	};

	State.subscribe((t) => {
		states = t;

		ActiveInverters = parseFloatPath(states, PUBLIC_ENVOY_ACTIVE_INVERTERS);
		NetInstantConsumption = parseFloatPath(states, PUBLIC_ENVOY_NET_INSTANT_CONSUMPTION);
		TotalInstantConsumption = parseFloatPath(states, PUBLIC_ENVOY_TOTAL_INSTANT_CONSUMPTION);

		InstantProduction = 0;
		if (NetInstantConsumption < TotalInstantConsumption) {
			InstantProduction = TotalInstantConsumption - NetInstantConsumption;
		}

		let value = parseFloatPath(states, PUBLIC_BOILER_CONSUMPTION) * 230;
		BoilerConsumption = value > 500 ? value : 0;

		BoilerTemperature = parseFloatPath(states, PUBLIC_BOILER_TEMPERATURE);
		BoilerFlow = parseFloatPath(states, PUBLIC_BOILER_FLOW) / 8000;

		RoomTemperature = 0; //parseFloat(states?.get(`${PUBLIC_MQTT_NODE_ID}/1/110002/3`)?.value || '0');

		VMCState = parseBooleanPath(states, PUBLIC_VMC_STATE);

		HeaterConsumption = 0; //parseFloat(states?.get(`${PUBLIC_MQTT_NODE_ID}/1/110002/7`)?.value || '0');

		HomeConsumption = TotalInstantConsumption;
		OtherConsumption = HomeConsumption - HeaterConsumption - CarConsumption - BoilerConsumption;
		OtherConsumption = OtherConsumption > 0 ? OtherConsumption : 0;

		NetInstantConsumptionStyle =
			NetInstantConsumption <= 0
				? 'stroke-dasharray:5;animation:dash-reverse 5s linear infinite;'
				: 'stroke-dasharray:5;animation:dash 5s linear infinite;';

		OtherConsumptionStyle =
			OtherConsumption > 0 ? 'stroke-dasharray:5;animation:dash 5s linear infinite;' : '';

		HeaterConsumptionStyle =
			HeaterConsumption > 0 ? 'stroke-dasharray:5;animation:dash 5s linear infinite;' : '';

		BoilerConsumptionStyle =
			BoilerConsumption > 0 ? 'stroke-dasharray:5;animation:dash 5s linear infinite;' : '';

		CarConsumptionStyle =
			CarConsumption > 0 ? 'stroke-dasharray:5;animation:dash 5s linear infinite;' : '';

		InstantProductionStyle =
			InstantProduction > 0 ? 'stroke-dasharray:5;animation:dash 5s linear infinite;' : '';

		BoilerFlowStyle = BoilerFlow > 0 ? 'stroke-dasharray:5;animation:dash 5s linear infinite;' : '';

		if (NetInstantConsumption <= 0) {
			HomeColor = Green;
		} else if (InstantProduction > 0) {
			HomeColor = Orange;
		} else {
			HomeColor = Red;
		}

		Temperature = parseFloatPath(states, PUBLIC_TEMPERATURE);
		Humidity = parseFloatPath(states, PUBLIC_HUMIDITY);
		GarageState = Math.floor(parseFloatPath(states, PUBLIC_GARAGE_STATE) / 10);
	});

	onMount(async () => {
		Connect();

		settings = Cookies.get('settingsButton') == 'false' ? false : true;
	});

	const sendCommand = (
		nodeId: number,
		endpointId: number,
		clusterId: number,
		id: number,
		type: Type,
		value: string
	) => {
		netError = false;

		fetch(`${PUBLIC_API_ENDPOINT}/api/commands`, {
			method: 'POST',
			headers: {
				Authorization: 'Bearer ' + token
			},
			body: JSON.stringify({
				Id: id,
				NodeId: nodeId,
				EndpointId: endpointId,
				ClusterId: clusterId,
				Type: type,
				Value: value
			})
		})
			.then((res) => {
				if (!res.ok) {
					if (res.status === 403) {
						BackToLogin();
					}
				}
			})
			.catch(() => {
				netError = true;
				BackToLogin();
			});
	};

	const triggerHapticFeedback = () => {
		if (window.AndroidHaptic) {
			window.AndroidHaptic.vibrate(100); // Vibrates for 100ms
		}
	};

	const sendVMC = () => {
		let nodeId = NodeIds.get('VMC');
		if (nodeId == undefined) {
			return;
		}

		triggerHapticFeedback();

		sendCommand(nodeId, 1, 6, VMCState ? 2 : 1, Type.Null, 'null');
	};

	const sendGarage = () => {
		let nodeId = NodeIds.get('Garage');
		if (nodeId == undefined) {
			return;
		}

		triggerHapticFeedback();

		sendCommand(nodeId, 1, 258, GarageState > 0 ? 1 : 0, Type.Null, 'null');
	};
</script>

